                  style={{padding:"40px 100px 20px 100px"}}
                  gutter={[{ xs: 8, sm: 16, md: 24, lg: 32 },{ xs: 8, sm: 16, md: 24, lg: 32 }]}
                >
                    {DisplqyMenuPageArray}
                </Row>
            </div>
        </div>
    )
}


export default TestPage;
