import React, { useState } from 'react';
import { Map, GoogleApiWrapper,Marker,InfoWindow } from "google-maps-react"
import { Row, Col,Typography,Carousel } from 'antd';
const { Title } = Typography;
const style = { background: '#0092ff', padding: '8px 0' };

const array = ['안양','안양','안양','안양','안양','안양','안양','안양','안양','안양','안양','안양','안양','안양','안양','안양','안양']

const GuidanceStore=()=>{

    const [eventlist,setEventlist] = useState([]);
    const [showingInfoWindow,setShowingInfoWindow] = useState(false);
    const [activeMarker,setActiveMarker] = useState({});
    const [selectedPlace,setSelectedPlace] = useState({});
    const [infosindow,setInfosindow] = useState("");
    const [redirect,setRedirect] = useState(false);


    const onmarkerclick = (props,marker, e)=>{
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true,
            infosindow:marker.name,
            redirect:true
        });
      }

      const onmapclick = (props)=>{
        if(this.state.showingInfoWindow){
            this.setState({
                showingInfoWindow:false,
                activeMarker:null
            })
        }
    }




    const DisplayArray = array.map((result,key)=>{
        return(
            <Col span={6}>
                <div style={style}>
                    <Title level={2}>
                        {result}
                    </Title>
                </div>
            </Col>
        )
    })

    return(
        <div style={{backgroundColor:"#000000"}} >
            <Carousel autoplay effect="fade" style={{textAlign: "center",height: "350px",lineHeight: "200px",background: "#364d79",overflow: "hidden"}}>
                <div>
                <h3>1</h3>
                </div>
                <div>
                <h3>2</h3>
                </div>
                <div>
                <h3>3</h3>
                </div>
                <div>
                <h3>4</h3>
                </div>
            </Carousel>
            <div style={{backgroundColor:"#ffffff", height:"100%"}}>
                  <Map
                    google = {this.props.google}
                    zoom={area.whkvy} //14
                    style={mapStyles}
                    initialCenter={{ lat:area.ysung, lng:area.xsung }}
                    onClick={this.onmapclick}
                  >
                      {eventmarkers}
                      <Marker 
                        name={"Current location"}
                        title={"center maker"} 
                        position={{ lat:area.ysung, lng:area.xsung }}
                        onClick={this.onmarkerclick}
                      />
                        <InfoWindow 
                            marker={this.state.activeMarker}
                            visible={this.state.showingInfoWindow}
                            content="<div>"
                        >
                            <div id="iwc">
                            {this.state.infosindow}
                            <button>상세정보창으로 이동</button>
                            </div>
                        </InfoWindow>
                      {/* lat-mapy , lng-mapx */}
                  </Map>
              </div>
            <Row gutter={[{ xs: 8, sm: 16, md: 24, lg: 32 },{ xs: 8, sm: 16, md: 24, lg: 32 }]} style={{padding:"100px"}} >
               {DisplayArray}
            </Row>
        </div>
    )
}



export default GoogleApiWrapper({
    apiKey: "AIzaSyCgl0XB4EdBHtmyB4P7Pu4HbnM3gndzyiY"
})(GuidanceStore);
